const Moment = require("moment");

function mapIssueToView(i) {
    let vm = { ...i };
    
    let daysSLA = i.sla || 0;

    vm.sla_end_date = Moment(i.created_date).add(daysSLA, "days");
    vm.created_date = Moment(i.created_date); // Normalize date format from JSON file
    
    return vm;
}

module.exports = {
    mapIssueToView: mapIssueToView
};
